$(document).ready(function () {
    $("#profile__load__true").on("change", function ()
    {
        /*$("#profile__load__false").html($("#profile__load__true").value);
         $("#profile__load__false").setAttribute("title",$("#profile__load__true").value);*/
        var formFile = document.getElementById("profile__load__true");
        var divResult = document.getElementById("profile__load__false");
        var res = formFile.value.replace(/^([^\\\/]*(\\|\/))*/, "");
        if (res == "") {
            res = "Прикрепить файл";
        }
        divResult.innerHTML = "<a href='#'>" + res + "</a>";
        divResult.setAttribute("title", formFile.value);
    });

    $("#profile__load__false").on("click", function ()
    {
        document.getElementById('profile__load__true').click();
    });


    $('.street input.findStreet').keyup(function () {
        var path = $('#template_path').val();
        $(this).removeClass('set');
        $(this).data('tt', '');
        $.ajax({
            url: path + '/ajax/street.php?q=' + encodeURI($(this).val()),
            success: function (res) {
                $('.streetFinder').html('');
                res.forEach(function (item, i, arr) {
                    var elementJq = $('<div/>', {
                        text: item.NAME,
                        on: {
                            click: function (e) {
                                setStreet(item.XML_ID, e, item.NAME)
                            }

                        }
                    });
                    $('.streetFinder').append(elementJq);
                    $('.streetFinder').show();
                    $('.scroll_bar').show();

                });
                if (res.length > 5) {
                    $(".scroll_bar").mCustomScrollbar({
                        axis: "y",
                        theme: "dark",
                        alwaysShowScrollbar: 2,
                        scrollInertia: 0,
                        scrollbarPosition: "inside",

                    });
                } else {
                    $(".scroll_bar").mCustomScrollbar("destroy");
                }
                //$('.streetFinder').append(res);


                if (res.length == 0) {
                    $('.streetFinder').html('');
                    $('.streetFinder').hide();
                }
            }
        })
    })

    $('.street input.findStreet').blur(function () {
        setTimeout(function () {
            $('.streetFinder').hide();
            $('.scroll_bar').hide();
        }, 400)
    })
    $('.street input.findStreet').focus(function () {
        if ($('.streetFinder').find('div').length > 0 && !$(this).hasClass('set')) {
            $('.streetFinder').show();
            $('.scroll_bar').show();
        }
    })
    $('#arh_city').click(function (e) {
        $.cookie("BITRIX_SM_CITY", 'Архангельск', {path: '/', expires: 180});
    })
    $('#sevsk_city').click(function (e) {
        $.cookie("BITRIX_SM_CITY", 'Северодвинск', {path: '/', expires: 180});
    })
    $('a.set_adress').click(function (e) {

        e.preventDefault();
        $('#select_street input[required]').each(function () {
            $(this).removeClass('error');
        })
        if ($('#house').val().length == 0 || $('#flat').val().length == 0) {
            alert('Не все обязательные поля заполнены');
            $('#select_street input[required]').each(function () {
                if ($(this).val() == '')
                    $(this).addClass('error');
                else
                    $(this).removeClass('error');
            });
            return false;
        }
        if ($('.findStreet').data('tt').length == 0) {
            alert('Укажите верный адрес');
            $('.findStreet').addClass('error');
            return false;
        }
        var adress = $('.findStreet').val();
        if ($('#house').val().length > 0)
            adress += ' д.' + $('#house').val();
        if ($('#korpus').val().length > 0)
            adress += ' корп.' + $('#korpus').val();
        if ($('#flat').val().length > 0)
            adress += ' кв.' + $('#flat').val();

        if (adress.length > 0) {
            //if ($('.findStreet').data('tt') == "ТТ-55")
            //{
            $.cookie("BITRIX_TT", $('#stree_tt').val(), {path: '/', expires: 180});
            $.cookie("BITRIX_ADRESS", adress, {path: '/', expires: 180});
            $.cookie("BITRIX_HIDE_STREET", '', {path: '/'});
            $.ajax({
                url: '/bitrix/templates/main_adaptive/ajax/address.php',
                data: {
                    tt: $('#stree_tt').val(),
                    adress: adress,
                    hide: ''
                },
                success: function () {
                    window.location.assign('/?city=arh');
                }
            });

            //}

        }
    });
    $('.auth-city-yes').click(function () {
        $.cookie("BITRIX_SHOW_ATTENTION", '1', {path: '/', expires: 180});
        $(this).parent().parent().find('.closeSelectCity').click();
    });
    $('.auth-city-no').click(function (e) {
        e.preventDefault();
        $('#select_city').show();
        $('#select_street_auth').hide();
        $('a.address-selection').attr('id', 'set_adress');
        $.cookie("BITRIX_SHOW_ATTENTION", '1', {path: '/', expires: 180});
        $.cookie("BITRIX_SM_CITY", 'Архангельск', {path: '/', expires: -1});
        $.cookie("BITRIX_TT", '', {path: '/', expires: -1});
        $.cookie("BITRIX_ADRESS", '', {path: '/', expires: -1});
        $.cookie("BITRIX_HIDE_STREET", '', {path: '/', expires: -1});
    });
    $('.auth-sevsk-city-yes').click(function () {
        $.cookie("BITRIX_SEVSK_SHOW_CITY", '1', {path: '/', expires: 1});
        $(this).parent().parent().find('.closeSelectCity').click();
    });
    $('.auth-sevsk-city-no').click(function (e) {
        e.preventDefault();
        $('#select_city').show();
        $('#select_street_auth').hide();
        $.cookie("BITRIX_SEVSK_SHOW_CITY", '1', {path: '/', expires: 1});
        $.cookie("BITRIX_SM_CITY", 'Северодвинск', {path: '/', expires: -1});
    });
    function setStreet(TT, event, name) {
        $('.findStreet').data('tt', TT);
        $('#stree_tt').val(TT);
        $('.findStreet').val(name).addClass('set');
    }

    $(document).on('click', 'a#set_adress', function (e) {
        e.preventDefault();
        $('#select_street').show();
        $.cookie("BITRIX_SM_CITY", 'Архангельск', {path: '/', expires: 1});
        $.cookie("BITRIX_TT", '', {path: '/', expires: -1});
        $.cookie("BITRIX_ADRESS", '', {path: '/', expires: -1});
        $.cookie("BITRIX_HIDE_STREET", '', {path: '/', expires: -1});
        $('#select_city').hide();
    });


    $('#select_street .closeSelectCity').click(function () {
        //$.cookie("BITRIX_HIDE_STREET", 1, {path: '/', expires: 7});
        //window.location.assign('/?city=arh');

        $('#select_street').hide();
        $('#select_city').show();
    })

    $('.showAdressSelect').click(function () {
        $(".popupSelectCity").show();
        $('#select_street').show();
    });

    $('.clear_adress').click(function () {
        if (confirm('Вы действительно хотите удалить адрес ?')) {
            $.cookie("BITRIX_TT", '', {path: '/', expires: -1});
            $.cookie("BITRIX_ADRESS", '', {path: '/', expires: -1});
            $.cookie("BITRIX_HIDE_STREET", '', {path: '/', expires: -1});
            window.location.assign('/?city=arh');
        } else {
        }
    })
});
